from tkinter import *from tkinter import messageboxfrom calc_core import calc_coreimport threadingdef gui_process():    fkt = Tk()    fkt.geometry("375x590")    fkt.title("Simple Calculator With Voice")    fkt.iconbitmap("calculator.ico")    fkt.configure(bg="white")    fkt.resizable(0, 0)    #messagebox.showinfo(title = "Notification", message = "No Values Found..!!", )    #call core engine    engine = calc_core()    engine.dispaly(fkt)    fkt.bind('<KeyPress>', engine.keystrock)    fkt.bind('<Return>', engine.callback)    engine.set_menu(fkt)    engine.set_buttons(fkt)    #call Gui    fkt.mainloop()def voice_command():    engine = calc_core()    engine.txt()class calculator():    # initializing Tkinter & Setting Window    process_one = threading.Thread(target=gui_process)    #process_two = threading.Thread(target=voice_command)    process_one.setDaemon(True)    process_one.start()    #process_two.start()    process_one.join()    #process_two.join()