from tkinter import *from tkinter import messageboxfrom py_calculator.calc_core import calc_coreimport threadingdef gui_process():    fkt = Tk()    fkt.geometry("375x590")    fkt.title("Simple Calculator With Voice")    fkt.iconbitmap("calculator.ico")    fkt.configure(bg="white")    fkt.resizable(0, 0)    messagebox.showinfo(title = "Notification", message = "No Values Found..!!", )    #call core engine    engine = calc_core()    engine.dispaly(fkt)    fkt.bind('<KeyPress>', engine.keystrock)    fkt.bind('<Return>', engine.callback)    engine.set_menu(fkt)    engine.set_buttons(fkt)    #call Gui    fkt.mainloop()def voice_command():    engine = calc_core()    engine.txt()class calculator():    # initializing Tkinter & Setting Window    process_one = threading.Thread(target=gui_process)    #process_two = threading.Thread(target=voice_command)    process_one.start()    #process_two.start()    process_one.join()    #process_two.join()    queue = multiprocessing.Queue()    # creating GUI process    temp = 1    p1 = multiprocessing.Process(target=gui_process, args=(queue,))    # Start GUI process    p1.start()    # creating a new process    p = multiprocessing.Process(target=voice_command, args=(queue,))    # Starting the new process    p.start()    # Wait for the processes to finish    queue.close()    queue.join_thread()    p.join()    p1.join()